import React from 'react';

import { Card, Button } from 'react-bootstrap';

import { createMuiTheme, responsiveFontSizes, ThemeProvider, makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';

import Background from '../../laptop2.jpg';
import "./Home.css";

var sectionStyle = {
    width: "99vw",
    height: "60.313em",
    backgroundImage: `url(${Background})`
};

let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

const Home = () => (
    <div className="Home">
        <ThemeProvider theme={theme}>
            <section className="header" style={ sectionStyle }>
                <div className="header-text">
                    <Typography variant="h4" id="header-top-text">Automation and IT services for businesses of all shapes and sizes</Typography>
                    <Typography variant="h2" id="header-bottom-text">Increase business with quailty technical services, and increased online presence.</Typography>
                    <Typography variant="button" className="button">LET'S GET STARTED</Typography>
                </div>
            </section>
            <div className="lander">
                <Card id="newsletter-card" className="mx-auto">
                    <Card.Header className="card-header">Featured</Card.Header>
                    <Card.Body>
                        <Card.Title>Stay in the know by joining our newsletter!</Card.Title>
                        <Card.Text>
                        With supporting text below as a natural lead-in to additional content.
                        </Card.Text>
                        <Button variant="primary">Go somewhere</Button>
                    </Card.Body>
                </Card>
            </div>
        </ThemeProvider>
    </div>
);

export default Home;
